import React from "react";

export default function Textform(props) {
  return (
    <div>
      <h1>{props.heading}</h1>
    <div class="mb-3">
      <textarea class="form-control" id="myBox" rows="8"></textarea>
    </div>
    <button className="btn btn-primary">Convert to UpperCase</button>
    </div>
  );
}
