import React, {useState} from 'react'

export default function Textform(props) {
  const handleUpClick=()=>{
    // console.log("Uppercase was clicked "+text);
    let newText=text.toUpperCase();
    setText(newText);
    props.showAlert('Converted to UpperCase','success');
  }
  const handleLoClick=()=>{
    // console.log("Uppercase was clicked "+text);
    let newText=text.toLowerCase();
    setText(newText);
    props.showAlert('Converted to LowerCase','success');
  }
  const handleCleClick=()=>{
    // console.log("Uppercase was clicked "+text);
    setText(text.replace(text,""));
    props.showAlert('Clear text successfully','success');
  }
  const handleExtraClick=()=>{
    let newtext=text.replace(/\s+/g,' ').trim();
    setText(newtext);
    props.showAlert('Extra spaces removed successfully','success');
  }

  const handleOnChange=(event)=>{
    // console.log("On change");
    setText(event.target.value);
  }
  const [text, setText]=useState('');
  // text='Updated text' //wrong way to update state
  // setText("new text")  //right way to update state
    return (
      <>
    <div className="container" style={{color:props.mode==='dark'?'white':'black'}}> 
      <h1>{props.heading}</h1>
    <div className="mb-3">
      <textarea className="form-control" style={{backgroundColor:props.mode==='dark'?'grey':'white',
    color:props.mode==='dark'?'white':'black'}} value={text}  onChange={handleOnChange} id="myBox" rows="8"></textarea>
    </div>
    <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to UpperCase</button>
    <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to LowerCase</button>
    <button className="btn btn-primary mx-2" onClick={handleCleClick}>Clear Text</button>
    <button className="btn btn-primary mx-2" onClick={handleExtraClick}>Remove Extra Spaces</button>
    </div>
    <div className="container my-2" style={{color:props.mode==='dark'?'white':'black'}}>
      <h2>Manipulated Text</h2>
      <p>no. of characters are {text.length} and words are {text.split(" ").length}</p>
      <h2>Preview</h2>
      <p>{text.length===0?'Enter text here to preview it':text}</p>
    </div>
    </>
  );
}
