import React, {useState} from 'react'

export default function Textform(props) {
  const handleUpClick=()=>{
    // console.log("Uppercase was clicked "+text);
    let newText=text.toUpperCase();
    setText(newText);
  }
  const handleLoClick=()=>{
    // console.log("Uppercase was clicked "+text);
    let newText=text.toLowerCase();
    setText(newText);
  }
  const handleCleClick=()=>{
    // console.log("Uppercase was clicked "+text);
    setText(text.replace(text,""));
  }
  const handleHover=()=>{
    // console.log("Uppercase was clicked "+text);
    button:{
      color:green;
    }
  }

  const handleOnChange=(event)=>{
    // console.log("On change");
    setText(event.target.value);
  }
  const [text, setText]=useState('');
  // text='Updated text' //wrong way to update state
  // setText("new text")  //right way to update state
    return (
      <>
    <div className="container"> 
      <h1>{props.heading}</h1>
    <div className="mb-3">
      <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
    </div>
    <button className="btn btn-danger mx-2" onClick={handleUpClick}>Convert to UpperCase</button>
    <button className="btn btn-danger mx-2" onClick={handleLoClick}>Convert to LowerCase</button>
    <button className="btn btn-danger mx-2" onClick={handleCleClick} onMouseEnter={handleHover}>Clear Text</button>
    </div>
    <div className="container my-2">
      <h2>My Work</h2>
      <p>no. of characters are {text.length} and words are {text.split(" ").length}</p>
      <h2>Preview</h2>
      <p>{text}</p>
    </div>
    </>
  );
}
